var https = require('https');
var fs = require('fs');
https.get('https://user.lufax.com/user/captcha/captcha.jpg?source=login&_=1397472027558', function(res) {
  console.log("statusCode: ", res.statusCode);
  console.log("headers: ", res.headers);

  var buffer = new Buffer(0, 'binary');

  res.on('data', function(chunk) {
     buffer = Buffer.concat([buffer, new Buffer(chunk,'binary')]);
  });


  res.on('end', function(){
	fs.writeFile('test.png', buffer);
  });
}).on('error', function(e) {
  console.error(e);
});
